import React from "react";
import { Col, Container, Image, Row } from "react-bootstrap";
import star from '../assets/star.svg';

const DevicePage = () => {
    const device = { id: 1, name: 'Iphone 12 pro', price: 25000, rating: 5, img: 'https://img5.lalafo.com/i/posters/original/e3/35/d5/8b003b74c299624a509534d4af.jpeg' }
    return (
        <Container className="mt-3">
            <Col md={4}>
                <Image width={300} height={300} src={device.img} />
            </Col>
            <Col md={4}>
                <Row>
                    <h2>{device.name}</h2>
                    <div
                        className="d-flex justify-content-center align-items-center"
                        style={{background: 'url(${star}) no-repeat center center', width:240, height:240, ba}}
                    >
                        {device.rating}
                    </div>
                </Row>
            </Col>

        </Container>
    )
}

export default DevicePage;
